&{gwmi Win32_OperatingSystem | Select Caption, BuildNumber, Version, SystemDirectory |fl ; gwmi -Class Win32_BaseBoard | Format-List Manufacturer, Product, SerialNumber, Version ; gwmi -Class Win32_BIOS | Select-Object BIOSVersion,Manufacturer,SMBIOSBIOSVersion | fl  ; Get-CimInstance Win32_Processor | fl ; gwmi -ClassName win32_VideoController | Select-Object Description,DriverVersion,AdapterCompatibility,InfFilename,VideoModeDescription  | Format-List ; gwmi Win32_PhysicalMemory | Format-Table Manufacturer, SerialNumber, BankLabel, DeviceLocator, @{n="Size (GB)"; e={($_.Capacity/1GB)}; align="center"}, @{n="ClockSpeed (MHz)"; e={($_.ConfiguredClockSpeed)}; align="center"} -Auto ; Get-PhysicalDisk ; wmic /namespace:\\root\wmi path MSStorageDriver_FailurePredictStatus ; wmic startup get caption,command ; Get-WmiObject win32_service | where { $_.Caption -notmatch "Microsoft" -and $_.PathName -notmatch "Windows" } | Select-Object Name,Startmode,State,Status,ExitCode,ProcessId,DisplayName | Sort-Object -Property @{Expression = "Name" } |ft } | Out-File -Width 4096  $env:USERPROFILE\desktop\system.txt